import React, { useState } from 'react';
import './App.css';

function App() {
  const [calc, setCalc] = useState('')
  const [result, setResult] = useState('')
  const ops = ['/', '*', '+', '-', '.'];
  const updateCalc = value => {
    if( ops.includes(value) && calc === '' || ops.includes(value) && ops.includes(calc.slice(-1))){
      return;
    }
    if(!ops.includes(value)){
      setResult(eval(calc + value).toString())
    }
    setCalc(calc + value)
  }

  const calculate = () => {
    setCalc(eval(calc).toString())
  }
  const deleteOne = () => {
    if(calc == ''){
      return;
    }
    const value = calc.slice(0, -1);
    setCalc(value)
  }
  const deleteAll = () => {
    if(calc == ''){
      return;
    }
    setCalc('')
    setResult('')
  }

  // const createDigits = () => {
  //   const digits = []

  //   for(let i = 1; i < 10; 1 + 1){
  //     digits.push(
  //       <button onClick={() => updateCalc(i.toString()) } key={i}>{i}</button>
  //     )
  //   }
  //   return digits
  // }
  return (
    <div class="container">
        <div class="calculadora">
            <div class="display">
                {result ? <span>({result})</span> : '' }
                {calc || '0'}
                <div id="valor__actual"></div>
            </div>
            <button onClick={deleteAll} class="col-2">C</button>
            <button onClick={deleteOne} class="numero">&larr;</button>
            <button onClick={() => updateCalc('%') } class="operador">%</button>
            <button onClick={() => updateCalc('7') }class="numero">7</button>
            <button onClick={() => updateCalc('8') } class="numero">8</button>
            <button onClick={() => updateCalc('9') } class="numero">9</button>
            <button onClick={() => updateCalc('*') } class="operador">X</button>
            <button onClick={() => updateCalc('4') } class="numero">4</button>
            <button onClick={() => updateCalc('5') } class="numero">5</button>
            <button onClick={() => updateCalc('6') } class="numero">6</button>
            <button onClick={() => updateCalc('-') } class="operador">-</button>
            <button onClick={() => updateCalc('1') } class="numero">1</button>
            <button onClick={() => updateCalc('2') } class="numero">2</button>
            <button onClick={() => updateCalc('3') } class="numero">3</button>
            <button onClick={() => updateCalc('+') } class="operador">+</button>
            <button onClick={() => updateCalc('0') } class="col-2">0</button>
            <button onClick={() => updateCalc('.') } class="numero">.</button>
            <button onClick={()=> calculate} class="operador">=</button>
        </div>
    </div>
  );
}

export default App;
